<script>
    export let status;
    let counterStatus = 0;

    $: if(status){
        if(status == 'name') {
            counterStatus = 0
        } else if (status == 'address') {
            counterStatus = 1
        } else if (status == 'time'){
            counterStatus = 2
        } else if (status == 'contacts'){
            counterStatus = 3
        } else if (status == 'delivery'){
            counterStatus = 4
        } else if (status == 'requisites'){
            counterStatus = 5
        } else if (status == 'finish'){
            counterStatus = 6
        }
    }
</script>

