<script>
    export let showModal;
    export let toggleModal;

    let inputLink;
    let linkToShare = 'https://www.rozarioapp.ru/1234';
    let isCopied = false;

    const copyLink = () => {
        inputLink.select();
        inputLink.setSelectionRange(0, 99999);
        document.execCommand("copy");
        isCopied = true;
    }
</script>

<section class="coupon__share-modal {showModal ? ' coupon__share-modal--show' : ''}">
